import axios from "axios";
import { createContext, useEffect, useState } from "react";

export const ApiContext = createContext();

const ApiContextProvider = (props) => {
  const [accessToken, setAccessToken] = useState("");
  const [success, setSuccess] = useState(false);

  //Posts
  const [allJobs, setAllJobs] = useState([]);

  //Users
  const [userDetail, setUserDetail] = useState({});
  const [userAllDetails, setUserAllDetails] = useState({});

  //UserInfos
  const [userInfo, setUserInfo] = useState([]);

  //Inbox
  const [userInbox, setUserInbox] = useState([]);

  // SignUp
  const signUp = (user) => {
    axios({
      url: "api/user/create",
      method: "POST",
      data: user,
    })
      .then((response) => {
        console.log(response.data.success);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  //https://snyk.io/advisor/npm-package/react-cookie/functions/react-cookie.Cookies
  const userLogin = async (user) => {
    const aa = axios.create({});
    const response = await aa.post("user/login", user);
    setSuccess(response.data.success);
    if (response.data.success) {
      setAccessToken((a) => response.headers.access_token);
      localStorage.setItem("access_token", response.headers.access_token);
      localStorage.setItem("refresh_token", response.headers.refresh_token);
      getUserDetail();
      getUsersAllDetails();
    }
    return response;
  };

  //User Details
  const getUsersAllDetails = async () => {
    const access_token = localStorage.getItem("access_token");
    if (access_token !== "") {
        await axios
          .get("userProfile/user_profile", {
            headers: {
              Authorization: `Bearer ${access_token}`,
            },
          })
          .then((response) => {
            setUserAllDetails(response.data);
          })
          .catch(async (error) => {
            if (error.response.status === 401) {
              await refreshToken();
              getUsersAllDetails();
              return null;
            }
          });
    }
  };
  //User Root Infos
  const getUserDetail = async () => {
    const access_token = localStorage.getItem("access_token");
    await axios
      .post(
        `user/getuser`,
        {},
        {
          headers: {
            Authorization: `Bearer ${access_token}`,
          },
        }
      )
      .then((response) => {
        setUserDetail(response.data.data);
      })
      .catch(async (error) => {
        if (error.response.status === 401) {
          await refreshToken();
          getUserDetail();
        }
      });
  };

  // Get All Users Profile
  const getAllUsersProfile = async () => {
    const response = await axios.get("userProfile/getinfos");
    setUserInfo(response.data.data);
  };

  // Get Jobs Infos
  const getJobInfos = async () => {
    const response = await axios.get("new_post");
    setAllJobs(response.data.data);
  };
  const refreshToken = async () => {
    const response = await axios.post(
      "/user/refresh",
      {},
      {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("refresh_token"),
        },
      }
    );
    localStorage.setItem("access_token", response.headers.access_token);
  };

  //Get Inbox Messages
  const getInboxMessages = ()=> {
    if (localStorage.getItem("access_token")){
    axios.post("inbox/user", {}, {
      headers:{
        Authorization: "Bearer " + localStorage.getItem("access_token")
      }
    }).then(response=> {
      console.log(response.data.data)
      setUserInbox(response.data.data)
    }).catch(error=> {
      if (error.response.status === 401) {
        refreshToken()
        getInboxMessages()
      }else{
        console.log(error)
      }
    })
  }
}//Inbox Focused Message
const getInboxFocusedMessage = ()=> {
  const response = axios.get(`inbox/${inboxMessageID}`)
  return response.data.data
}






  useEffect(() => {}, [success]);
  useEffect(() => {
    getJobInfos();
    getAllUsersProfile();
    if (localStorage.getItem("access_token") !== null) {
      setAccessToken(() => localStorage.getItem("access_token"));
      setSuccess(() => true);
      getInboxMessages();
      getUserDetail();
      getUsersAllDetails();
    } else setSuccess(() => false);
  }, []);
  useEffect(() => {
    if (accessToken !== "") {
      setSuccess(() => true);
    } else setSuccess(() => false);
  }, [accessToken]);

  useEffect(() => {}, [userAllDetails]);
  useEffect(() => {}, [userInbox]);
  return (
    <ApiContext.Provider
      value={{
        userInfo,
        signUp,
        userLogin,
        success,
        accessToken,
        setAccessToken,
        setSuccess,
        allJobs,
        userDetail,
        userAllDetails,
        userInbox,
        getInboxFocusedMessage,
        
      }}
    >
      {props.children}
    </ApiContext.Provider>
  );
};
export default ApiContextProvider;
