import axios from "axios";
import { createContext, useEffect, useState } from "react";
export const ApiContext = createContext();

const ApiContextProvider = (props)=>{
    const [id, setId] = useState('')
    const [success, setSuccess] = useState(false)

    const [allPost, setAllPost] = useState([])
    
    const [userDetail, setUserDetail] = useState([])

    const getAllPost = async ()=> {
        const response = await axios.get('http://127.0.0.1:5000/api/new_post');
        setAllPost(response.data)
    }
    const getUserDetail = async (id)=> {
        const response = await axios.get(`http://127.0.0.1:5000/api/user/${id}`)
        setUserDetail(response.data.user)
    }

    const signUp = (user)=>{
        axios({
            url: "http://127.0.0.1:5000/api/user/create",
            method : "POST",
            data: user
        }).then( (response) => {
            console.log(response.data.success);
        }).catch(err => {
            console.log(err)
        })
    }

    const userLogin = async (user)=>{
        const response = await axios.post('http://127.0.0.1:5000/api/user/login', user)
        setSuccess(response.data.success)
        if (response.data.success) {
            getUserDetail(response.data.id)
            setId(response.data.id)
        }
        return response
    }
    useEffect(()=>{
        getAllPost()
    },[])
    useEffect(()=>{},[id, success])

    return (
        <ApiContext.Provider value={{signUp, userLogin, success, id, setId, setSuccess, allPost, userDetail}} >
            {props.children}
        </ApiContext.Provider>
      )
}
export default ApiContextProvider;
