import { useContext, useEffect } from "react";
import HeaderContent from "./headerContent/headerContent";
import CenterContent from "./centerContent/centerContent";
import LeftContent from "./leftContent/leftContent";
import RightContent from "./rightContent/rightContent";
import Modal from "./modal/modal";
import { ScriptsContext } from "../context/ScriptsContext";
import { ApiContext } from "../context/ApiContext";

const HomePage = () => {
    //////////////////////////Contexts///////////////////////////////////
  const {modalControl} = useContext(ScriptsContext)
  const {getAllPost} = useContext(ApiContext)
  
  return (
    <>
      <style>
        {`.animated {{
      -webkit-animation-duration: 1s;
      animation-duration: 1s;
      -webkit-animation-fill-mode: both;
      animation-fill-mode: both;
    }}

    .animated.faster {
      -webkit-animation-duration: 500ms;
      animation-duration: 500ms;
    }

    .fadeIn {
      -webkit-animation-name: fadeIn;
      animation-name: fadeIn;
    }

    .fadeOut {
      -webkit-animation-name: fadeOut;
      animation-name: fadeOut;
    }

    @keyframes fadeIn {
      from {
        opacity: 0;
      }

      to {
        opacity: 1;
      }
    }

    @keyframes fadeOut {
      from {
        opacity: 1;
      }

      to {
        opacity: 0;
      }
    }`}
      </style>
      <div className="bg-transparent bg-gradient-to-tl from-sh-dark-blue to-sh-light-blue">
        <HeaderContent />
        <div className="container flex flex-row  flex-nowrap">
          {/* left Content */}
          <LeftContent />
          {/* Center Content */}
          <CenterContent />
          {/* Right Content */}
          <RightContent />
        </div>
      </div>
      {/* Modal */}
      {modalControl ? <Modal />: null}
    </>
  );
};
export default HomePage;
