import { useContext } from "react";
import { ScriptsContext } from "../../context/ScriptsContext";
import { Link } from "react-router-dom";
import { ApiContext } from "../../context/ApiContext";

const IndexPage = () => {
    const { openModal, setModalNameW } =
    useContext(ScriptsContext);
    const {success} = useContext(ApiContext)
  return (
    <div
      className="flex  lg:basis-3/5 bg-sh-linen 
      md:rounded-t-large"
    >
      <div className="pt-12 flex flex-col w-full">
      <section class="h-96 lg:h-128 group relative">
    <img src="/images/homepage.png" alt="Main Image" class="h-full w-full object-cover" />

    <div class="absolute bottom-0 w-full bg-gradient-to-b from-transparent to-black">
      <div class="container pl-10">
        
        <h1 class="text-4xl lg:text-xl w-1/3 text-sh-skin capitalize group-hover:mb-1 duration-500">
        Find the perfect freelance services for your business
        </h1>
        <p class="text-sh-phlox group-hover:mb-2 duration-500 text-sm lg:text-base w-3/4 lg:w-2/3">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Sapiente quas officia aut quidem? Numquam fuga quis
          repellendus ad beatae culpa?
        </p>
        <div class="flex space-x-8 opacity-0 group-hover:opacity-100 group-hover:mb-10 lg:group-hover:mb-10 duration-1000">
          <div class="flex space-x-2 items-center cursor-pointer">
            <Link to={"/freelancers"} class="text-sh-phlox uppercase lg:text-lg hover:text-gega-red duration-500">Hire A Freelancer</Link>
            <div class="flex h-8 w-8 rounded-full text-center items-center justify-center bg-gega-red text-sh-phlox">
              <i class="fas fa-play"></i>
            </div>
          </div>

          <div class="flex space-x-2 items-center cursor-pointer">
            {!success ? <button type="button" onClick={()=>{
                setModalNameW("login");
                openModal();
            }} class="text-sh-phlox uppercase lg:text-lg hover:text-gega-red duration-500">Earn Money Freelancing</button> : 
            <Link to={"/jobs"} class="text-sh-phlox uppercase lg:text-lg hover:text-gega-red duration-500">Earn Money Freelancing</Link>}
            
            <div class="flex h-8 w-8 rounded-full text-center items-center justify-center bg-gega-red text-sh-phlox">
              <i class="fas fa-arrow-right"></i>
            </div>
          </div>
        </div>

      </div>
    </div>
  </section>
        <div className="grid md:grid-cols-2 lg:grid-cols-1 xl:grid-cols-2 p-24 w-full gap-10 max-h-1">
          <h1>Home Page</h1>
        </div>
      </div>
    </div>
  );
};
export default IndexPage;
