import { useEffect, useRef, useState } from "react";
import AddButton from "./addButton";
import RemoveButton from "./removeButton";

const NewPostModal = () => {
  //Steps
  const [stepCounter, setStepCounter] = useState([
    true,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
  ]);
  const [stepIndex, setStepIndex] = useState(1);
  useEffect(() => {}, [stepIndex]);
  ///////////////////////////////////////////////////////
  const moneyType = useRef()
  const title = useRef()
  const paymentAmount = useRef()
  const description = useRef()
  const file = useRef()
  const [stepTitleInput, setStepTitleInput] = useState()
  const [stepDescriptionInput, setStepDescriptionInput] = useState()
  const onTitleChange = (e) =>{
    setStepTitleInput({...stepTitleInput,
      [e.target.name]: e.target.value
    })
  }
  const onDescriptionChange = (e) =>{
    setStepDescriptionInput({...stepDescriptionInput,
      [e.target.name]: e.target.value
    })
  }
  //
  const submitHandle = (e) => {
    e.preventDefault();
    const titles = []
    const descriptions = []
    const steps = []
    const bodyFormData = new FormData();
    bodyFormData.append("payment_method",[paymentAmount.current.value,moneyType.current.value])
    bodyFormData.append("title",title.current.value)
    bodyFormData.append("description",description.current.value)
    bodyFormData.append("files",file.current.value)
    for (const [key, value] of Object.entries(stepTitleInput)) {
      titles.push(value)
    }
    for (const [key, value] of Object.entries(stepDescriptionInput)) {
      descriptions.push(value)
    }
    titles.map((value, key) =>{
      steps.push([value, descriptions[key]])
    })
    bodyFormData.append("steps", steps)
    for (var pair of bodyFormData.entries()) {
      console.log(pair[0]+ ', ' + pair[1]); 
  }
  };
  ///////////////////////////////////////////////////////
  return (
    <form className="newPost" onSubmit={submitHandle}>
      <section
        className="px-4 flex flex-col bg-white rounded-r-3xl overflow-auto"
        style={{ width: "720px" }}
      >
        <h1 className="text-2xl mb-2">Post new job</h1>

        <div className="job-info border-b-2 py-2 mb-5">
          <div className="mb-4">
            <label
              className="block text-gray-700 text-sm mb-2"
              htmlFor="job-title"
            >
              Title
            </label>
            <input
              ref={title}
              className="appearance-none block w-full bg-white text-gray-700 border border-gray-400 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:border-gray-500"
              type="email"
              id="job-title"
              name="job-title"
              placeholder="Frontend Developer"
              autoFocus
            />
          </div>
          <div className="md:flex md:justify-between space-x-10">
            <div className="w-full mb-4 md:mb-0">
              <label
                htmlFor="from"
                className="block text-gray-700 text-sm mb-2"
              >
                Payment
              </label>
              <input
                ref={paymentAmount}
                type="text"
                className="appearance-none block w-full bg-white text-gray-700 border border-gray-400 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:border-gray-500"
                name="from"
                placeholder="Amount"
              />
            </div>
            <div className="w-full mb-4 md:mb-0">
              <label
                htmlFor="from"
                className="block text-gray-700 text-sm mb-2"
              >
                {"\u00A0"}
              </label>
              <select
              ref={moneyType}
                id="countries"
                class="appearance-none block w-full bg-white text-gray-700 border border-gray-400 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:border-gray-500"
              >
                <option selected>Choose a type</option>
                <option value="$">$ Dolar</option>
                <option value="£">£ Pound</option>
                <option value="€">€ Euro</option>
                <option value="₺">₺ Symbol</option>
              </select>
            </div>
          </div>
          <div>
            <label
              htmlFor="description"
              className="block text-gray-700 text-sm mb-2"
            >
              Description
            </label>
            <textarea
            ref={description}
              name="description"
              cols=""
              rows="8"
              className="w-full border border-gray-400 rounded"
            ></textarea>
          </div>
          <AddButton
            setStepCounter={setStepCounter}
            setStepIndex={setStepIndex}
            stepCounter={stepCounter}
            stepIndex={stepIndex}
          ></AddButton>
          <span>Add Step - (Max 8)</span>
          <div id="Steps">
            {stepCounter.map((step, index) => {
              if (step) {
                return (
                  <div className="flex flex-wrap -mx-3" key={index}>
                    <div className="w-full md:w-1/2 px-3 mb-4 md:mb-0">
                      <div className="flex flex-col">
                        <div>
                          <label
                            htmlFor={`title-name-${index}`}
                            className="block text-gray-700 text-sm mb-2"
                          >
                            Step Title
                          </label>
                          <input
                            onChange={onTitleChange}
                            type="text"
                            className="appearance-none block w-full bg-white text-gray-700 border border-gray-400 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:border-gray-500"
                            name={`title-name-${index}`}
                            placeholder="Title"
                          />

                          <RemoveButton
                            setStepCounter={setStepCounter}
                            setStepIndex={setStepIndex}
                            stepCounter={stepCounter}
                            stepIndex={stepIndex}
                            currentIndex={index}
                          />
                          <span>Remove Step </span>
                        </div>
                      </div>
                    </div>

                    <div className="w-full md:w-1/2 px-3 mb-4 md:mb-0">
                      <label
                        htmlFor={`title-description-${index}`}
                        className="block text-gray-700 text-sm mb-2"
                      >
                        Step Description
                      </label>
                      <textarea
                      onChange={onDescriptionChange}
                        name={`title-description-${index}`}
                        cols=""
                        rows="4"
                        className="w-full border border-gray-400 rounded"
                      ></textarea>
                    </div>
                  </div>
                );
              }
            })}
          </div>
          <div className="mb-4 md:mb-0">
            <label
              htmlFor="company-logo"
              className="block text-gray-700 text-sm mb-2"
            >
              Logo Image
            </label>
            <input
              ref={file}
              type="file"
              className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-3 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
              id="company-logo"
              name="company-logo"
            />
          </div>
        </div>
        <div>
          <button
            className="bg-teal-500 hover:bg-teal-600 text-white py-2 px-3 rounded"
            type="submit"
          >
            Create job
          </button>
        </div>
      </section>
    </form>
  );
};
export default NewPostModal;
