import axios from "axios";
import { createContext, useEffect, useState } from "react";
export const ApiContext = createContext();

const ApiContextProvider = (props)=>{
    const [accessToken, setAccessToken] = useState('')
    const [success, setSuccess] = useState(false)

    const [allPost, setAllPost] = useState([])
    
    const [userDetail, setUserDetail] = useState([])

    const getAllPost = async ()=> {
        const response = await axios.get('/new_post');
        setAllPost(response.data) 
    }
    const getUserDetail = async (id)=> {
        const response = await axios.get(`/user/${id}`)
        setUserDetail(response.data.user)
    }

    const signUp = (user)=>{
        axios({
            url: "api/user/create",
            method : "POST",
            data: user
        }).then( (response) => {
            console.log(response.data.success);
        }).catch(err => {
            console.log(err)
        })
    }

    const userLogin = async (user)=>{
        const response = await axios.post('user/login', user);
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + response.data.access_token
        setSuccess(response.data.success)
        if (response.data.success) {
            getUserDetail(response.data.id)
            setAccessToken(response.data.access_token)
        }
        return response
    }
    useEffect(()=>{},[accessToken, success])
    useEffect(()=>{getAllPost()},[])

    return (
        <ApiContext.Provider value={{signUp, userLogin, success, accessToken, setAccessToken, setSuccess, allPost, userDetail}} >
            {props.children}
        </ApiContext.Provider>
      )
}
export default ApiContextProvider;
