import axios from "axios";
import { createContext, useEffect, useState } from "react";
import ReactDOM from "react"
export const ApiContext = createContext();

const ApiContextProvider = (props)=>{
    const [id, setId] = useState('')
    const [success, setSuccess] = useState(false)

    const signUp = (user)=>{
        axios({
            url: "http://127.0.0.1:5000/api/user/create",
            method : "POST",
            data: user
        }).then( (response) => {
            console.log(response.data.success);
        }).catch(err => {
            console.log(err)
        })
    }

    const userLogin = async (user)=>{
        const response = await axios.post('http://127.0.0.1:5000/api/user/login', user)
        setSuccess(response.data.success)
        console.log(response.data.success,'api')
        return response.data.success
         /* axios({
            url: "http://127.0.0.1:5000/api/user/login",
            method : "POST",
            data: user
        }).then((response) => {
            console.log(response.data.success,"api")
            console.log(response.data.id)
            setSuccess(response.data.success)
        }).catch(err => {
            console.log(err)
        }) */
    }
    useEffect(() => {
        console.log(success)
    },[success])

    return (
        <ApiContext.Provider value={{signUp, userLogin, success, id, setId, setSuccess}} >
            {props.children}
        </ApiContext.Provider>
      )
}
export default ApiContextProvider;
