const ApiContextProvider = (props)=>{
    const [id, setId] = useState('')
    const [success, setSuccess] = useState(false)

    const signUp = (user)=>{
        axios({
            url: "http://127.0.0.1:5000/api/user/create",
            method : "POST",
            data: user
        }).then(response => {
            console.log(response.data.message)
        }).catch(err => {
            console.log(err)
        })
    }

    const userLogin =  (user)=>{
        let success1 = false
        axios.post('http://127.0.0.1:5000/api/user/login',user).then(response => {
            const response1 = response.data.message
        })
        console.log(response1)
         /* axios({
            url: "http://127.0.0.1:5000/api/user/login",
            method : "POST",
            data: user
        }).then( (response) => {
            console.log(response.data.success,"api")
            console.log(response.data.id)
             setSuccess(response.data.success)
             success1 = true
        }).catch(err => {
            console.log(err)
        }) */
        return success1
    }
    return (
        <ApiContext.Provider value={{signUp, userLogin, success, id, setId}} >
            {props.children}
        </ApiContext.Provider>
      )
}
export default ApiContextProvider;
