import { createContext, useEffect, useRef, useState } from "react";
import FreelancerMenu from "../component/leftContent/freelancerMenu";
import InboxModal from "../component/modal/inboxModal";
import JobsMenu from "../component/leftContent/jobsMenu";
import LoginModal from "../component/modal/loginModal";
import MessageModal from "../component/modal/messageModal";
import NewPostModal from "../component/modal/newPostModal";
import SignUpModal from "../component/modal/signUpModal";
import UserMenu from "../component/rightContent/userMenu";
import FilterList from "../component/rightContent/filterList";
import UsersFilter from "../component/rightContent/usersFilter";
export const ScriptsContext = createContext();

const ScriptsContextProvider = (props)=>{
    const [modalControl, setModalControl] = useState(false)
  const openModal = ()=>{
    if(modalControl){
        setModalControl(false);
    }else{
        setModalControl(true);
    }
  }
  ///////////////////////////////////////////////////////////////////////////////////////
  //Modal Close
   ///////////////////////////////////////////////////////////////////////////
  //Modals
  const [modalComponents] = useState({
    login: <LoginModal />,
    signup: <SignUpModal />,
    inbox: <InboxModal />,
    newPost: <NewPostModal />,
    message: <MessageModal />,
  });
  const [modalName, setModalName] = useState("");
  const changeModal = (nameOfModal) => {
    setModalName(nameOfModal);
  };
  const [searchBar] = useRef()
  ////////////////////////////////////////////////////////////////////
  //Left Menu Change
  const [jobFreelanceMenu] = useState({
    freelance: <FreelancerMenu />,
    jobs: <JobsMenu />,
  });
  const [menuName, setMenuName] = useState("jobs");
  const leftMenuChange = (nameOfMenu) => {
    setMenuName(nameOfMenu);
  }; ////////////////////////////////////////////////////////////////
  //Right Menu Change
  const [userOtherMenu, setUserOtherMenu] = useState({
    "menuItem": <UserMenu />,
    "menuName": "userInfo",
  });
  const [userOtherMenuName, setUserOtherMenuName] = useState("userInfo");
  const rightMenuChange = () => {
    if (userOtherMenuName === "userInfo") {
      setUserOtherMenu({
        "menuItem": <FilterList />,
        "menuName": "other",
      });
      setUserOtherMenuName("other");
    } else {
      setUserOtherMenu({
        "menuItem": <UserMenu />,
        "menuName": "userInfo",
      });
      setUserOtherMenuName("userInfo");
    }
  };//////////////////////////////////////////////////////////////////////////
  const rightMenuChangeUsers = () => {
    if (userOtherMenuName === "userInfo") {
      setUserOtherMenu({
        "menuItem": <UsersFilter />,
        "menuName": "other",
      });
      setUserOtherMenuName("other");
    } else {
      setUserOtherMenu({
        "menuItem": <UserMenu />,
        "menuName": "userInfo",
      });
      setUserOtherMenuName("userInfo");
    }
  };//////////////////////////////////////////////////////////////////////////
  ///Alert Hidden
  const [alertHidden, setAlertHidden] = useState(false)
  const changeAlert = () => {
    setAlertHidden(true);
    setTimeout(() => {setAlertHidden(false);}, 2000)
  }
  useEffect(() => {}, [menuName, modalName, userOtherMenuName,modalControl, alertHidden]);

  return (
    <ScriptsContext.Provider value={{modalControl,openModal, modalComponents, modalName,changeModal,rightMenuChangeUsers,
          userOtherMenu,rightMenuChange,leftMenuChange, menuName, jobFreelanceMenu, changeAlert, alertHidden, searchBar}} >
        {props.children}
    </ScriptsContext.Provider>
  )
}
export default ScriptsContextProvider;
